listitem.screen:
- name: Register with SCC and activate modules hosts: all vars: suseconnect_base_product: key: '{{ sles_registration_key }}' product: '{{ ansible_distribution }}'
suseconnect_subscriptions: - {name: "sle-module-containers", state: enabled} - {name: "PackageHub", state: disabled} - {name: "sle-module-python3", state: enabled}
tasks: - name: Register system and modules with SUSE Customer Center ansible.builtin.include_role: name: suseconnect

- name: Adding or deleting modules and extensions
  hosts: all
  vars:
    suseconnect_subscriptions:
    - {name: "sle-module-containers", state: enabled}
    - {name: "PackageHub", state: disabled}
    - {name: "sle-module-python3", state: enabled}
    - {name: "sle-ha", state: enabled, key: "REGISTRATION-KEY"}

  tasks:
    - name: Remove other subscriptions
      ansible.builtin.include_role:
        name: suseconnect
