section.screen:
#!/bin/bash # combustion: network prepare set -euxo pipefail
nm_config() { umask 077 # Required for NM config mkdir -p /etc/NetworkManager/system-connections/ cat >/etc/NetworkManager/system-connections/static.nmconnection <<-EOF [connection] id=static type=ethernet autoconnect=true
[ipv4] method=manual dns=192.168.100.1 address1=192.168.100.42/24,192.168.100.1 EOF }
if [ "${1-}" = "--prepare" ]; then nm_config # Configure NM in the initrd exit 0 fi
# Redirect output to the console exec > >(exec tee -a /dev/tty0) 2>&1
nm_config # Configure NM in the system curl example.com # Leave a marker echo "Configured with combustion" > /etc/issue.d/combustion
